/**
* @Class Player
* 
*/
function Player(obj) {
	this.id = obj.id;
	this.name = obj.name || 'unknown';
	this.host = obj.host;
	this.sign = obj.sign || null;
	
	//this.score = 0;
	
	this.number = null;
	
	this.socket = obj.socket;
	
	this.setSign = function(str) {
		var setted = false;
		if(str === 'o' || str === 'x') {
			this.sign = str;
			setted = true;
		}
		return setted;
	}
	
	this.roomId = null;
}

module.exports = Player;