exports.showUserInfo = function(socket, str) {
	var space = function() {console.log('');}
	space();
	console.log('===========================');
    
    if(str) {
        console.log('+ '+str+':');
        space();
    }
    
    console.log('+ [id] ' + socket.id);
    console.log('+ [host] ' + socket.handshake.headers.host);
    console.log('===========================');
}